import { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';


function App() {

  const [books, setBooks] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [bookmarks, setBookmarks] = useState([]);

  const [error, setError] = useState("");

  const [count, setCount] = useState("")
  const [name, setName] = useState("");
  const [author, setAuthor] = useState("");
  const [newReview, setReview] = useState("")


  const fetchBooks = async() => {
    try {      
      const response = await axios.get("/api/books");
      setBooks(response.data.books);
    } catch(error) {
      setError("error retrieving books: " + error);
    }
  }
  const createBook = async() => {
    try {
      await axios.post("/api/books", {name: name, author: author});
    } catch(error) {
      setError("error adding a book: " + error);
    }
  }
  const deleteOneBook = async(book) => {
    try {
      await axios.delete("/api/books/" + book.id);
    } catch(error) {
      setError("error deleting a book" + error);
    }
  }

  useEffect(() => {
    fetchBooks();
  },[]);

  const addBook = async(e) => {
    e.preventDefault();
    await createBook();
    fetchBooks();
    setName("");
    setAuthor("");
    setReview("");
    setCount("");
  }

  const deleteBook = async(book) => {
    await deleteOneBook(book);
    fetchBooks();
  }

  const fetchFavorites = async() => {
    try {      
      const response = await axios.get("/api/favorites");
      console.log(response.data);
      setFavorites(response.data.favorites);
    } catch(error) {
      setError("error retrieving favorites: " + error);
    }
  }
  const createFavorite = async(book) => {
    try {
      await axios.post("/api/favorites", {name: book.name, author: book.author, review: newReview});
    } catch(error) {
      setError("error adding a favorite: " + error);
    }
  }
  const deleteOneFavorite = async(fave) => {
    try {
      await axios.delete("/api/favorites/" + fave.id);
    } catch(error) {
      setError("error deleting a favorite" + error);
    }
  }

  useEffect(() => {
    fetchFavorites();
  },[]);

  const addFave = async(e) => {
    await createFavorite(e);
    fetchFavorites();
    setName("");
    setAuthor("");
    setReview("");
    setCount("");
  }

  const deleteFave = async(fave) => {
    await deleteOneFavorite(fave);
    fetchFavorites();
  }

  const fetchBookmarks = async() => {
    try {      
      const response = await axios.get("/api/bookmarks");
      console.log(response.data);
      setBookmarks(response.data.bookmarks);
    } catch(error) {
      setError("error retrieving bookmarks: " + error);
    }
  }

  const createBookmark = async(book) => {
    try {
      await axios.post("/api/bookmarks", {name: book.name, author: book.author, pagenumber: '0'});
    } catch(error) {
      setError("error adding a bookmark: " + error);
    }
  }
  const deleteOneBookmark = async(bm) => {
    try {
      await axios.delete("/api/bookmarks/" + bm.id);
    } catch(error) {
      setError("error deleting a bookmark" + error);
    }
  }

  const updateBookPage = async(bm) => {
    try {
      console.log("Updating with " + bm);
      await axios.put("/api/bookmarks", {id: bm.id, pagenum:bm.pagenumber});
    } catch(error) {
      setError("error deleting a bookmark" + error);
    }
  }

  useEffect(() => {
    fetchBookmarks();
  },[]);

  const addBm = async(e) => {
    await createBookmark(e);
    fetchBookmarks();
    setName("");
    setAuthor("");
    setReview("");
    setCount("");
  }

  const deleteBm = async(bm) => {
    await deleteOneBookmark(bm);
    fetchBookmarks();
  }

  const updatePageNum = async(bm) => {
    await updateBookPage(bm);
    fetchBookmarks();
  }


  return (
    <div className="App">
      {error}
      <div>
      <h1>Create a Book</h1>
      <form onSubmit={addBook}>
        <div>
          <label>
            Title:
            <input type="text" value={name} onChange={e => setName(e.target.value)} />
          </label>
        </div>
        <div>
          <label>
            Author:
            <input type="text" value={author} onChange={e=>setAuthor(e.target.value)} />
          </label>
        </div>
        <input type="submit" value="Submit" />
      </form>
      <h1>All Books</h1>
      {books?.map( book => (
        <div key={book.id} className="book">
          <div className="bookDescription">
            <p>{book.name}</p>
            <p><i>By: {book.author}</i></p>
          </div>
          <label>
            Review:
            <input type="text" value={newReview} onChange={e=>setReview(e.target.value)} />
            <button onClick={e => addFave(book)}>Add To Favorites With Review</button>
          </label>
          <button onClick={e => deleteBook(book)}>Delete</button>
          <button onClick={e => addBm(book)}>Start Reading</button>
        </div>
      ))}
      </div>

      <div>
      <h1>Favorite Books</h1>
      {favorites?.map( fave => (
        <div key={fave.id} className="book">
          <div className="bookDescription">
            <p>{fave.name}</p>
            <p><i>By: {fave.author}</i></p>
            <p>My Review: {fave.review}</p>
          </div>
          <button onClick={e => deleteFave(fave)}>Remove From Favorites</button>
        </div>
      ))}
      </div>


      <div>
      <h1>Bookmarks</h1>
      {bookmarks?.map( bm => (
        <div key={bm.id} className="book">
          <div className="bookDescription">
            <p>{bm.name}</p>
            <p><i>By: {bm.author}</i></p>
            <p>On Page: {bm.pagenumber}</p>
          </div>
          <label>
            Page Number:
            <input type="text" value={count} onChange={e=>setCount(e.target.value)} />
            <button onClick={e => updatePageNum(bm)}>Update</button>
          </label>
          <button onClick={e => deleteBm(bm)}>Stop Reading</button>
        </div>
      ))}
      </div>
    </div>
  );
}

export default App;
